package com.imooc.diveinspringboot.jl.spring.controller;import com.imooc.diveinspringboot.jl.spring.model.Text;import com.imooc.MyBatisUtil;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.web.bind.annotation.*;import org.apache.ibatis.session.SqlSession;import com.imooc.diveinspringboot.ICatagoryOperation;import java.util.List;@RestController@EnableAutoConfiguration@CrossOrigin(origins = "*", maxAge = 3600)public class TextRestController {    @RequestMapping(value = "/search/insert", method = RequestMethod.POST)    public int addContext(@RequestParam("content") String content) {		SqlSession sqlSession = MyBatisUtil.getSqlSession();		ICatagoryOperation icatagoryOperation = sqlSession.getMapper(ICatagoryOperation.class);		int result = icatagoryOperation.insertCatagory(content);		if (result > 0) {			sqlSession.commit();			System.out.println("添加成功 Yesd isidis");		} else			sqlSession.rollback();		MyBatisUtil.closeSession(sqlSession);        return result;    }    @RequestMapping(value = "/search/{textKey}", method = RequestMethod.GET)    public List<Text> getAllKey(@PathVariable String textKey) {        SqlSession sqlSession = MyBatisUtil.getSqlSession();        ICatagoryOperation icatagoryOperation = sqlSession.getMapper(ICatagoryOperation.class);        List<Text> list = icatagoryOperation.findAllByLikeContent(textKey);        MyBatisUtil.closeSession(sqlSession);        return list;    }}